mapper.select:
SELECT draw, first, second, third, fourth, fifth, sixth, bonus, first_prize AS firstPrize, second_prize AS secondPrize, third_prize AS thirdPrize, fourth_prize AS fourthPrize, draw_date FROM lotto_draw_history
where draw = #{draw}
order by draw desc

select idx ,result , regd as regDate , story , snid from ( select sug.idx ,sug.snid ,num.number_set as result ,story , date_format(regd, '%Y-%m-%d %H:%i') as regd , TIMESTAMPDIFF(SECOND, regd, now()) as td from lotto_sug_test sug left outer join lotto_suggestion_numset num on sug.snid = num.idx
where sug.snid not in (select distinct nid from lotto_draw_simmulation) and isShow = 1
) tmp

mapper.update:
update lotto_suggestion_numset
set total_prize = #{totalPrize}
where idx = #{idx}

mapper.select:
select idx , replace(rep,'<','&lt;') as story , regd
,CASE WHEN td < 60 THEN 'now' WHEN td >= 60 and td <=3600 THEN concat(round(td/60),' min') WHEN td >= 3600 and td <=86400 THEN concat(round(td/3600),' hour') WHEN td >= 86400 and td <=604800 THEN concat(round(td/86400),' day') WHEN td >= 604800 and td <=2419200 THEN concat(round(td/604800),' week') WHEN td >= 2419200 THEN concat(round(td/2419200),' month') END as timer
from(
select
snid as idx
sid as idx
,rep , date_format(reg_date, '%Y-%m-%d %H:%i') as regd , TIMESTAMPDIFF(SECOND, reg_date, now()) as td
from lotto_suggestion_reply where snid = #{idx}
from lotto_story_reply where sid = #{idx}
and iss = 1 order by reg_date desc ) tmp

select count(*) as cnt

    from lotto_suggestion_reply
    where snid = #{idx}


    from lotto_story_reply
    where sid = #{idx}

    and iss = 1
